package bitcask

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestIsValidKey(t *testing.T) {
	assert.True(t, IsValidKey("12345"), "123456 should be a valid key")
	assert.True(t, IsValidKey("abc@45"), "abc@45 should be a valid key")
	assert.True(t, IsValidKey("item:0002"), "item:0002 should be a valid key")
	assert.False(t, IsValidKey(" 12345"), " 123456 should be a invalid key")
	assert.False(t, IsValidKey("?12345"), "?12345 should be a invalid key")
	assert.False(t, IsValidKey("@12345"), "@12345 should be a invalid key")
	assert.False(t, IsValidKey(""), " should be a invalid key")
	assert.False(t, IsValidKey(""), " len=251 should be a invalid key")
}
